)

setOnlineHandler(handleOnline => {
  if (isServer || !window?.addEventListener) {
    return
  }

  // Listen to online
  window.addEventListener('online', handleOnline, false)

  return () => {
    // Be sure to unsubscribe if a new handler is set
    window.removeEventListener('online', handleOnline)
  }
})
import React from 'react'

import {
  queryCache as defaultQueryCache,
  queryCaches,
  makeQueryCache,
} from '../core'
import { QueryCache } from '../core/queryCache'

export const queryCacheContext = React.createContext(defaultQueryCache)

export const useQueryCache = () => React.useContext(queryCacheContext)

export interface ReactQueryCacheProviderProps {
  queryCache?: QueryCache
}

export const ReactQueryCacheProvider: React.FC<ReactQueryCacheProviderProps> = ({
  queryCache,
  children,
}) => {
  const resolvedQueryCache = React.useMemo(
    () => queryCache || makeQueryCache(),
    [queryCache]
  )

  React.useEffect(() => {
    queryCaches.push(resolvedQueryCache)

    return () => {
      // remove the cache from the active list
      const i = queryCaches.indexOf(resolvedQueryCache)
      if (i > -1) {
        queryCaches.splice(i, 1)
      }
      // if the resolvedQueryCache was created by us, we need to tear it down
      if (queryCache == null) {
        resolvedQueryCache.clear({ notify: false })
      }
    }
  }, [resolvedQueryCache, queryCache])

  return (
    <queryCacheContext.Provider value={resolvedQueryCache}>
      {children}
    </queryCacheContext.Provider>
  )
}
import React from 'react'

import { mergeReactQueryConfigs } from '../core/config'
import { ReactQueryConfig } from '../core/types'

const configContext = React.createContext<ReactQueryConfig | undefined>(
  undefined
)

export function useContextConfig() {
  return React.useContext(configContext)
}

export interface ReactQueryConfigProviderProps {
  config: ReactQueryConfig
}

export const ReactQueryConfigProvider: React.FC<ReactQueryConfigProviderProps> = ({
  config,
  children,
}) => {
  const parentConfig = useContextConfig()

  const mergedConfig = React.useMemo(
    () =>
      parentConfig ? mergeReactQueryConfigs(parentConfig, config) : config,
    [config, parentConfig]
  )

  return (
    <configContext.Provider value={mergedConfig}>
      {children}
    </configContext.Provider>
  )
}
import React from 'react'

import { isServer } from '../core/utils'

export function useGetLatest<T>(obj: T): () => T {
  const ref = React.useRef<T>(obj)
  ref.current = obj
  return React.useCallback(() => ref.current, [])
}

function useIsMounted(): () => boolean {
  const mountedRef = React.useRef(false)
  const isMounted = React.useCallback(() => mountedRef.current, [])

  React[isServer ? 'useEffect' : 'useLayoutEffect'](() => {
    mountedRef.current = true
    return () => {
      mountedRef.current = false
    }
  }, [])

  return isMounted
}

export function useMountedCallback<T extends Function>(callback: T): T {
  const isMounted = useIsMounted()
  return (React.useCallback(
    (...args: any[]) => {
      if (isMounted()) {
        return callback(...args)
      }
    },
    [callback, isMounted]
  ) as any) as T
}

/**
 * This hook is a safe useState version which schedules state updates in microtasks
 * to prevent updating a component state while React is rendering different components
 * or when the component is not mounted anymore.
 */
export function useSafeState<S>(
  initialState: S | (() => S)
): [S, React.Dispatch<React.SetStateAction<S>>] {
  const isMounted = useIsMounted()
  const [state, setState] = React.useState(initialState)

  const safeSetState = React.useCallback(
    (value: React.SetStateAction<S>) => {
      scheduleMicrotask(() => {
        if (isMounted()) {
          setState(value)
        }
      })
    },
    [isMounted]
  )

  return [state, safeSetState]
}

export function useRerenderer() {
  const [, setState] = useSafeState({})
  return React.useCallback(() => setState({}), [setState])
}

/**
 * Schedules a microtask.
 * This can be useful to schedule state updates after rendering.
 */
function scheduleMicrotask(callback: () => void): void {
  Promise.resolve()
    .then(callback)
    .catch(error =>
      setTimeout(() => {
        throw error
      })
    )
}
import React from 'react'

import { useQueryCache } from './ReactQueryCacheProvider'
import { useSafeState } from './utils'

export function useIsFetching(): number {
  const queryCache = useQueryCache()

  const [isFetching, setIsFetching] = useSafeState(queryCache.isFetching)

  React.useEffect(
    () =>
      queryCache.subscribe(() => {
        setIsFetching(queryCache.isFetching)
      }),
    [queryCache, setIsFetching]
  )

  return isFetching
}
